import { NextApiRequest, NextApiResponse } from 'next'
import { NaiveBayes } from '../../shared/class/NaiveBayes'

let csvToJson = require('convert-csv-to-json')

/**
 * @description api
 * @param req
 * @param res
 */
const nb = (req: NextApiRequest, res: NextApiResponse) => {
  /* Convert csv to json */
  const data = csvToJson
    .fieldDelimiter(',')
    .getJsonFromCsv('./shared/data/banknote_authentication.csv')

  const nb = new NaiveBayes()
  nb.group(data)
  let predictions = nb.predict(data)

  let zero = 0
  let once = 0

  for (let i = 0; i < predictions.length; i++) {
    if (predictions[i] == 0) zero++
    else once++
  }

  console.log(zero + ' ' + once)
  console.log(predictions.length)

  res.send(JSON.stringify(data, null, 2))
}

export default nb
