const labelToInt = (obj) => {
  let size = 0
  let tmp = {}

  for (let key in obj) {
    if (obj.hasOwnProperty(key)) {
      tmp[size] = obj[key]
      delete obj[key]
      size++
    }
  }

  return tmp
}

export class NaiveBayes {
  obj: Object
  label: Array<any>
  nOfAttributes: number

  constructor() {
    this.obj = {}
    this.label = []
  }

  /**
   * @description this method seperates data based on class
   * @param data
   */
  group = (data: Array<string | number>) => {
    for (let i = 0; i < data.length; i++) {
      let tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa

      if (!this.obj[tmp]) {
        this.label.push([this.label.length, tmp])
        this.obj[tmp] = []
      }

      /* Attributes of class */
      let attributes = Object.keys(data[i])
      let type = attributes.pop() // save and remove, e.g. "species" from object

      if (data[i][type] === tmp) {
        data[i][type] = this.label.length - 1
        this.obj[tmp].push(data[i])
      }
    }

    /* Rename labels to integer */
    this.obj = labelToInt(this.obj)

    for (let key in this.obj) {
      if (this.obj.hasOwnProperty(key)) {
        this.nOfAttributes = Object.keys(this.obj[key][0]).length
      }
    }

    /* Push data and known labels to fit for training */

    this.fit(this.obj, this.label)
  }

  /**
   * @description this method helps train the model
   * @param x
   * @param y
   */
  fit = (X: Object, y: Array<number>): void => {
    /* Go through each object and store as label */
    for (let key in X) {
      if (X.hasOwnProperty(key)) {
        let labelKey = y[key][0]
        if (labelKey == key) {
          this.label[key] = []

          let emptyArr = new Array(this.nOfAttributes).fill(0)

          this.label[key].push({
            id: key,
            data: X[key],
            mean: emptyArr,
            stdev: emptyArr,
            prob: 0
          })
        }
      }
    }

    /* Calculate mean */
    for (let i = 0; i < this.label.length; i++) {
      let e = this.label[i][0]
      let data = e.data
      console.log(e)
      for (let j = 0; j < data.length; j++) {
        // for (let k = 0; k < this.nOfAttributes; k++) {
        // e.mean[k] += data[j][k]
        // console.log(data[j]['sepal_length'])
        // }

        let ii = 0
        for (let key in data[j]) {
          if (data[j].hasOwnProperty(key)) {
            e.mean[ii] += parseInt(data[j][key])
            ii++
          }
        }
      }
    }
  }

  predict(): number {
    return null
  }

  accuracy_score(): number {
    return null
  }
}
