export class NaiveBayes {
  obj: Object
  arr: Array<string | number>

  constructor() {
    this.obj = {}
    this.arr = []
  }

  /**
   * @description this method seperates data based on class
   * @param data
   */
  group = (data: Array<string | number>) => {
    let tmp = ''

    for (let i = 0; i < data.length; i++) {
      tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa
      if (!this.obj[tmp]) {
        this.obj[tmp] = []
      }

      /* Attributes of class */
      let attributes = Object.keys(data[i])
      let type = attributes.pop() // save and remove, e.g. "species" from object

      if (data[i][type] === tmp) this.obj[tmp].push(data[i])
