import { NextApiRequest, NextApiResponse } from 'next'
let csvToJson = require('convert-csv-to-json')

/**
 * @description calculate mean
 * @param arr
 */
const mean = (arr: Array<number | string>) => {}

/**
 * @description api
 * @param req
 * @param res
 */
const nb = (req: NextApiRequest, res: NextApiResponse) => {
  let classes = new Map() // storing classes

  /* Convert csv to json */
  const rawJson = csvToJson
    .fieldDelimiter(',')
    .getJsonFromCsv('./shared/data/iris.csv')

  let headers: Array<string> = null // table headers, e.g. sepal_length

  /* Retrieve classes */
  for (let i = 0; i < rawJson.length; i++) {
    /* We only want to store it once and not every loop, why it has ! since it's initiated as null */
    if (!headers) {
      headers = Object.keys(rawJson[i])
      headers.pop() // remove last element, e.g. species... again, only works if it's stored last in table
    }

    /* Stores last object value. Will only work when class is stored last in table, thus last in object. For dynamic purpose. */
    let tmp =
      rawJson[i][Object.keys(rawJson[i])[Object.keys(rawJson[i]).length - 1]] // class name, e.g. Iris-setosa
    classes.set(
      tmp,
      /* Will store attributes as properties in map object */
      headers.reduce((acc, cur) => {
        acc[cur] = null
        return acc
      }, {})
      /* E.g. 'Iris-virginica' => { sepal_length: null, sepal_width: null, petal_length: null, petal_width: null } */
    )
  }

  /* Map -> Array ... it has the classes names */
  let arr = Array.from(classes.keys())

  console.log(classes)

  res.send(JSON.stringify([...classes]))
}

export default nb
