export class NaiveBayes {
  obj: Object
  type: string // e.g. "species"

  constructor() {
    this.obj = {}
  }

  /**
   * @description this method seperates data based on class
   * @param data
   */
  group = (data: Array<string | number>) => {
    for (let i = 0; i < data.length; i++) {
      /* Stores last object value. Will only work when class is stored last in table, thus last in object. For dynamic purpose. */
      let tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa

      /* Attributes of class */
      let attributes = Object.keys(data[i])
      this.type = attributes.pop() // save and remove, e.g. "species" from object

      if (!this.obj[tmp]) {
        /* Create object and store table headers*/
        this.obj[tmp] = attributes.reduce((acc, cur) => {
          acc[cur] = null
          return acc
        }, {})
      }
      this.fit(tmp, data[i])
    }
  }

  /**
   * @description this method helps train the model
   * @param x
   * @param y
   */
  fit = (x, y): void => {
    let i = 0
    if (y[this.type] === x) i++
    console.log(i)
    this.obj[x].sepal_length += parseFloat(y['sepal_length'])
    console.log(i)
  }

  predict(): number {
    return null
  }

  accuracy_score(): number {
    return null
  }
}
