export class NaiveBayes {
  obj: Object

  constructor() {
    this.obj = {}
  }

  train(data: Array<string | number>) {
    let headers: Array<string> = null // table headers, e.g. sepal_length

    for (let i = 0; i < data.length; i++) {
      /* We only want to store it once and not every loop, why it has ! since it's initiated as null */
      if (!headers) {
        headers = Object.keys(data[i])
        headers.pop() // remove last element, e.g. species..., only works for datasets with class at the end, not optimal, but works for iris and banknote
      }

      /* Stores last object value. Will only work when class is stored last in table, thus last in object. For dynamic purpose. */
      let tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa

      /* If certain object does not exist, create it */
      if (!this.obj[tmp])
        /* Create object and store table this.headers */
        this.obj[tmp] = headers.reduce((acc, cur) => {
          acc[cur] = null
          return acc
        }, {})
    }
  }

  fit(x: string, y: number): void {
    //
  }

  predict(): number {
    return null
  }

  accuracy_score(): number {
    return null
  }
}
