import { NextApiRequest, NextApiResponse } from 'next'
let csvToJson = require('convert-csv-to-json')

/**
 * @description calculate mean
 * @param arr
 */
const mean = (arr: Array<number | string>) => {}

/**
 * @description api
 * @param req
 * @param res
 */
const nb = (req: NextApiRequest, res: NextApiResponse) => {
  let classes = new Map() // storing classes

  /* Convert csv to json */
  const rawJson = csvToJson
    .fieldDelimiter(',')
    .getJsonFromCsv('./shared/data/iris.csv')

  let headers = null // table headers, e.g. sepal_length

  /* Retrieve classes */
  for (let i = 0; i < rawJson.length; i++) {
    let tmp = '' // class name, e.g. Iris-setosa
    if (!classes.has(rawJson[i].species)) {
      /* Stores last object value. Will only work when class is stored last in table, thus last in object. For dynamic purpose. */
      tmp =
        rawJson[i][Object.keys(rawJson[i])[Object.keys(rawJson[i]).length - 1]]

      classes.set(tmp, null)
    }

    /* We only want to store it once and not every loop */
    if (!headers) {
      headers = Object.keys(rawJson[i])
      headers.pop() // remove tmp, e.g. species... again, only works if it's stored last in table
    }
  }

  /* Map -> Array */
  let arr = Array.from(classes.keys())

  //   mean(rawJson)

  res.json(headers)
}

export default nb
