export class NaiveBayes {
  obj: Object
  arr: Array<string | number>

  constructor() {
    this.obj = {}
    this.arr = []
  }

  /**
   * @description this method seperates data based on class
   * @param data
   */
  group = (data: Array<string | number>) => {
    let arr = []
    let obj = {}
    let tmp = ''

    for (let i = 0; i < data.length; i++) {
      tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa
      if (!obj[tmp]) {
        obj[tmp] = []
      }
    }

    for (let j = 0; j < Object.keys(obj).length; j++) {
      for (let k = 0; k < data.length; k++) {
        /* Attributes of class */
        let attributes = Object.keys(data[k])
        let type = attributes.pop() // save and remove, e.g. "species" from object

        if (Object.keys(obj)[j] === data[k][type])
          obj[Object.keys(obj)[j]].push(data[k])
      }
    }
  }

