import { NextApiRequest, NextApiResponse } from 'next'
let csvToJson = require('convert-csv-to-json')

/* TypeScript Class */
import { NaiveBayes } from '../../shared/class/NaiveBayes'

const accuracy_score = (
  pred: Array<number>,
  data: Array<number>,
  type: string
): Object => {
  let correct: number = 0

  for (let i = 0; i < pred.length; i++) {
    if (pred[i] == data[i][type]) correct++
  }

  return {
    correctly_classified: correct,
    examples: pred.length,
    accuracy: (correct / data.length) * 100
  }
}

/**
 * @description api
 * @param req
 * @param res
 */
const nb = (req: NextApiRequest, res: NextApiResponse) => {
  /* Convert csv to json */
  const data = csvToJson
    .fieldDelimiter(',')
    .getJsonFromCsv('./shared/data/banknote_authentication.csv')
  // .getJsonFromCsv('./shared/data/iris.csv')

  const nb = new NaiveBayes()
  nb.group(data)
  let predictions = accuracy_score(nb.predict(data), data, nb.type)

  res.send(JSON.stringify(predictions, null, 2))
}

export default nb
