export class NaiveBayes {
  obj: Object
  label: Array<any>

  constructor() {
    this.obj = {}
    this.label = []
  }

  /**
   * @description this method seperates data based on class
   * @param data
   */
  group = (data: Array<string | number>) => {
    let labels = []

    for (let i = 0; i < data.length; i++) {
      let tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa

      if (!this.obj[tmp]) {
        this.label.push([this.label.length, tmp])
        this.obj[tmp] = []
      }

      /* Attributes of class */
      let attributes = Object.keys(data[i])
      let type = attributes.pop() // save and remove, e.g. "species" from object

      if (data[i][type] === tmp) {
        data[i][type] = this.label.length - 1
        this.obj[tmp].push(data[i])
      }
    }

    let size = 0

    for (let key in this.obj) {
      if (this.obj.hasOwnProperty(key)) {
        this.obj[size] = this.obj[key]
