export class NaiveBayes {
  obj: Object
  rawData: Array<string | number>
  type: string

  constructor() {
    this.obj = {}
  }

  /**
   * @description this method seperates data based on class
   * @param data
   */
  seperate(data: Array<string | number>) {
    this.rawData = data

    for (let i = 0; i < data.length; i++) {
      /* Stores last object value. Will only work when class is stored last in table, thus last in object. For dynamic purpose. */
      let tmp = data[i][Object.keys(data[i])[Object.keys(data[i]).length - 1]] // class name, e.g. Iris-setosa

      /* If certain object does not exist, create it */
      if (!this.obj[tmp]) {
        /* Attributes of class */
        let attributes = Object.keys(data[i])
        this.type = attributes.pop()

        /* Create object and store table this.headers */
        this.obj[tmp] = attributes.reduce((acc, cur) => {
          acc[cur] = null
          return acc
        }, {})
      }
    }
  }

  /**
   * @description this method trains the model
   * @param x
   * @param y
   */
  fit(x, y): void {
    //
  }

  predict(): number {
    return null
  }

  accuracy_score(): number {
    return null
  }
}
